// eslint-disable : lint 끄는 기능
import React, { useState } from "react";
import "./App.css";

function App() {
  return (
    <div className="App">
      <div className="black-nav">
        <h4>logo</h4>
      </div>
      <button>정렬버튼</button>
      <button>글수정</button>

      {title.map(function (a, i) {
        return (
          <div className="list" key={i}>
            <h4>
              남자코트추천<span>👍</span>0
            </h4>

            <p>2월 17일 발행</p>
            <button
              onClick={(e) => {
                let copy = [...title];
                // splice : 배열 삭제
                copy.splice(i, 1);
                setTitle(copy);
              }}
            >
              삭제
            </button>
          </div>
        );
      })}
      {/* input에 뭔가 입력시 코드실행하고 싶으면 onChange/onInput */}
      {/* input에 입력한 값 가져오는 방법 : 파라미터에 e(event객체)라고 적음/ e.target이라고 적으면 이벤트 발생한 html태그*/}
      <input
        onChange={(e) => {
          // 늦게 처리됨 (비동기처리)
          setText(e.target.value);
          console.log(text);
        }}
      />
      <button
        onClick={() => {
          let copy = [...title];
          // unshift : array 앞자리에 자료 넣기
          copy.unshift(text);

          setTitle(copy);
        }}
      >
        글발행
      </button>
      {/* if문 대신 삼항연산자 사용 */}
      {/* {조건식 ? 참일 때 실행할 코드 : 거짓일 때 실행할 코드} */}
      {/* 내가 쓴 풀이식 -> 
       <h4
          onClick={() => {
            setModal(modal + 1);
          }}
        >

      {modal % 2 == 1 ? <Modal /> : null} */}
      {/* 애플 코딩 정답 */}
      {modal == true ? (
        <Modal setTitle={setTitle} title={title} modalTitle={modalTitle} />
      ) : null}
    </div>
  );
}

// 컴포넌트 만드는 방법 1
// props 전송은 부모 -> 자식만 가능 app -> modal
function Modal(props) {
  return (
    <div className="modal">
      {/* state가 Modal,App에서 필요하면 App에 만들어야함(최상위 컨포넌트) */}
      <h4>{props.title[props.modalTitle]}</h4>
      <p>날짜</p>
      <p>상세내용</p>
      {/* <button
        onClick={() => {
          let copy = [...props.title];
          copy[0] = "여자코트추천";
          props.setTitle(copy);
        }}
      >
        글수정
      </button> */}
    </div>
  );
}
// 컴포넌트 만드는 방법 2
//let Modal = ()=>{}

// class로 컴포넌트 만들기
//constructor,super,render 채워넣어야함

export default App;
