// eslint-disable : lint 끄는 기능
import { useState } from "react";
import "./App.css";

function App() {
  // state를 나중에 쓰고 싶다면 자료를 뽑는 문법을 적는다.
  // let [a,b] -> destructuring문법
  // 변수 vs state : 변수는 갑자기 변경되면 html에 자동으로 반영이 안됨, state는 자동 재렌더링됨
  let [title, setTitle] = useState([
    "남자코트추천",
    "강남 우동맛집",
    "파이썬독학",
  ]);
  let [good, setGood] = useState(0, good + 1);

  function like() {}

  return (
    <div className="App">
      <div className="black-nav">
        <h4>logo</h4>
      </div>
      <div className="list">
        <h4>
          {title[0]}
          {/* onClick={함수명만, 함수 만드는 문법 전체를 바로 넣어도 됨} */}
          <span
            onClick={() => {
              setGood = good + 1;
            }}
          >
            👍
          </span>{" "}
          {setGood}
        </h4>
        <p>2월 17일 발행</p>
      </div>

      <div className="list">
        <h4>{title[1]}</h4>
        <p>2월 17일 발행</p>
      </div>
      <div className="list">
        <h4>{title[2]}</h4>
        <p>2월 17일 발행</p>
      </div>
    </div>
  );
}
export default App;
